import {React,useState} from "react";
import { RouterProvider, createBrowserRouter, createRoutesFromElements, Route, Navigate } from "react-router-dom"
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";

function App() {

  const[contact,setContact]=useState([{name:"Eunice",phone:123456,email:"abc@email.com"},
  {name:"Oscar",phone:234455,email:"ocs@email.com"},]);


  const[appointment,setAppointment]=useState([{name:"Eunice",contactdate:"3-jan",time:"3pm"},
    {name:"Oscar",contactdate:"4-june",time:"4pm"},]);

 const addContact=((newContact)=>{
  
  setContact((prev)=>{

  return [newContact,...prev]


  })

  const addAppointment=((newAppointment)=>{

     setAppointment((prev)=>{
      return[newAppointment,...prev]
     }
   )



  })


 })






  

  const router = createBrowserRouter(createRoutesFromElements(
    <Route path="/" element={ <Root/> }>
      <Route index element={ <Navigate to={ROUTES.CONTACTS} replace/> }/>
      <Route path={ROUTES.CONTACTS} element={ <ContactsPage contact={contact} addContact={addContact} />  }/>
      <Route path={ROUTES.APPOINTMENTS} element={ <AppointmentsPage contact={contact} appointment={appointment} addAppointment={addAppointment}/>  }/>
    </Route>
  ));
  
  return (
    <RouterProvider router={router}/>
  );
}

export default App;
