import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = (props) => {
  
const[name,setName]=useState("");
const[phone,setPhone]=useState("")
const[email,setEmail]=useState("")
const[duplicateName,setDuplicateName]=useState(false)


const contactListName=props.contact;
const addContact=props.addContact;

useEffect(()=>{

 if(contactListName.indexOf(name)){

  setDuplicateName(true);

 }else{

  setDuplicateName(false)

 }
  

},[name])


  const handleSubmit = (e) => {
    e.preventDefault();
    setName(e.target.value)
    
    /*
    Add contact info and clear data
    if the contact name is not a duplicate
    */
    if(duplicateName===false){
   
      addContact({name:name,phone:phone,email:email})
      

    }

    setName("")
    setPhone("")
    setEmail("")

  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
        <ContactForm name={name} setName={setName} phone={phone} 
        setPhone={setPhone} email={email} setEmail={setEmail} handleSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList contact={props.contact}/>
      </section>
    </div>
  );
};
